import React, { useState, useEffect} from 'react';
import Header from '../components/Header';
import Sidebar from '../components/Sidebar';
import Footer from '../components/Footer';

const Profile = ({ userInfo, handleLogout }) => {

    const [posts, setPosts] = useState([]);

    // Fetch user profile on component mount
    useEffect(() => {
        const fetchProfile = async () => {
            try {
                const response = await fetch('/superadmin/FetchUserProfile', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json',
                    },
                    body: JSON.stringify({ user_id:userInfo.data.user_id }),
                });

                if (response.ok) {
                    const data = await response.json();
                    console.log('Profile data:', data);
                    setPosts(data);
                } else {
                    setErrorMessage('Failed to fetch profile');
                    console.error('Failed to fetch profile:', response.statusText);
                }
            } catch (error) {
                setErrorMessage('An error occurred while fetching the profile');
                console.error('Error:', error);
            }
        };

        if (userInfo.data.user_id) {
            fetchProfile();
        } else {
            console.error('User ID not found in userInfo');
        }
    }, [userInfo.data.user_id]);

    // Update profile
    const [username, setUpdateUname] = useState(posts.data?.username || '');
    const [email_id, setUpdateEmail] = useState(posts.data?.email_id || '');
    const [phone_no, setUpdatePhone] = useState(posts.data?.phone_no || '');
    const [password, setUpdatePassword] = useState(posts.data?.password || '');
    const [errorMessage, setErrorMessage] = useState('');

    const addProfileUpdate = async (e) => {
        alert(  JSON.stringify({ username, email_id, phone_no, password }), 'add charger all data' );
        e.preventDefault();
        try {
            const phoneNo = parseInt(phone_no);
            const Password = parseInt(password);
            const response = await fetch('/superadmin/UpdateUserProfile', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ user_id:userInfo.data.user_id, username, email_id, phone_no:phoneNo, password:Password, modified_by:userInfo.dada.username}),
            });
            alert(  JSON.stringify({ user_id:userInfo.data.user_id, username, email_id, phone_no:phoneNo, password:Password, modified_by:userInfo.dada.username}), 'add charger all data' );
            if (response.ok) {
                alert('Profile updated successfully');
                setUpdateUname(''); 
                setUpdateEmail(''); 
                setUpdatePhone(''); 
                setUpdatePassword(''); 
            } else {
                setErrorMessage('Failed to profile updated');
            }
        }catch (error) {
                setErrorMessage('An error occurred while profile updated');
                console.error('Error:', error);
        }
    };
    // Add Chargers end

    return (
        <div className='container-scroller'>
            {/* Header */}
            <Header userInfo={userInfo} handleLogout={handleLogout}/>
            <div className="container-fluid page-body-wrapper">
                {/* Sidebar */}
                <Sidebar/>
                <div className="main-panel">
                    <div className="content-wrapper">
                        <div className="row">
                            <div className="col-md-12 grid-margin">
                                <div className="row">
                                    <div className="col-12 col-xl-8 mb-4 mb-xl-0">
                                        <h3 className="font-weight-bold">Profile</h3>
                                    </div>
                                </div>
                            </div>
                        </div>
                        <div className="row">
                            <div className="col-lg-12 grid-margin stretch-card">
                                <div className="card">
                                    <div className="card-body">
                                        <div className="col-md-12 grid-margin stretch-card">
                                            <div className="card">
                                                <div className="card-body">
                                                    <form className="forms-sample" onSubmit={addProfileUpdate}>
                                                        <div className="form-group row">
                                                            <label for="exampleInputUsername2" className="col-sm-2 col-form-label">User Name</label>
                                                            <div className="col-sm-10">
                                                                <input type="text" className="form-control" placeholder="Username" value={username}  onChange={(e) => setUpdateUname(e.target.value)} required/>
                                                            </div>
                                                        </div>
                                                        <div className="form-group row">
                                                            <label for="exampleInputEmail2" className="col-sm-2 col-form-label">Email</label>
                                                            <div className="col-sm-10">
                                                                <input type="email" className="form-control" placeholder="Email" value={email_id}  onChange={(e) => setUpdateEmail(e.target.value)} readOnly required/>
                                                            </div>
                                                        </div>
                                                        <div className="form-group row">
                                                            <label for="exampleInputMobile" className="col-sm-2 col-form-label">Phone</label>
                                                            <div className="col-sm-10">
                                                                <input type="text" className="form-control" placeholder="Phone number" pattern="[0-9]{10}" value={phone_no}  onChange={(e) => setUpdatePhone(e.target.value)} required/>
                                                            </div>
                                                        </div>
                                                        <div className="form-group row">
                                                            <label for="exampleInputPassword2" className="col-sm-2 col-form-label">Password</label>
                                                            <div className="col-sm-10">
                                                                <input type="password" className="form-control" pattern="[0-9]{4}" placeholder="Password" value={password}  onChange={(e) => setUpdatePassword(e.target.value)} required/>
                                                            </div>
                                                        </div>
                                                        {errorMessage && <div className="text-danger">{errorMessage}</div>}
                                                        <div style={{textAlign:'center'}}>
                                                            <button type="submit" className="btn btn-primary mr-2">Update</button>
                                                        </div>
                                                    </form>
                                                </div>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                    {/* Footer */}
                    <Footer />
                </div>         
            </div>    
        </div>
    );
};   

export default Profile
