import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import SuperAdminApp from './adminApps/SuperAdminApp';
import ResellerAdminApp from './adminApps/ResellerAdminApp';
import ClientAdminApp from './adminApps/ClientAdminApp';
import Login from './reselleradmin/page/Login';
import Dashboard from './reselleradmin/page/Dashboard';
import ManageClient from './reselleradmin/page/ManageClient';
import ManageDevice from './reselleradmin/page/ManageDevice';
import ManageFinance from './reselleradmin/page/ManageFinance';
import Profile from './reselleradmin/page/Profile';

const App = () => {

  // Reseller Admin Authentication State
  const storedResellerAdminUser = JSON.parse(sessionStorage.getItem('resellerAdminUser'));
  const [resellerAdminLoggedIn, setResellerAdminLoggedIn] = useState(!!storedResellerAdminUser);
  const [resellerAdminUserInfo, setResellerAdminUserInfo] = useState(storedResellerAdminUser || {});
  // const [resellerAdminInitialLoad, setResellerAdminInitialLoad] = useState(true);

  const handleResellerAdminLogin = (data) => {
    const { email, ...rest } = data;
    setResellerAdminUserInfo({ email, ...rest });
    setResellerAdminLoggedIn(true);
    sessionStorage.setItem('resellerAdminUser', JSON.stringify({ email, ...rest }));
  };

  const handleResellerAdminLogout = () => {
    setResellerAdminLoggedIn(false);
    setResellerAdminUserInfo({});
    sessionStorage.removeItem('resellerAdminUser');
  };

  return (
    <Router>
      <Routes>
        {/* Super Admin Routes */}
        <Route path="/" element={<SuperAdminApp />} />

        {/* Reseller Admin Routes */}
        <Route
          path="/reselleradmin/*"
          element={
            resellerAdminLoggedIn ? (
              <Routes>
                <Route path="/" element={<Navigate to="/dashboard" />} />
                <Route path="/dashboard" element={<Dashboard userInfo={resellerAdminUserInfo} handleLogout={handleResellerAdminLogout} />} />
                <Route path="/managedevice" element={<ManageDevice userInfo={resellerAdminUserInfo} handleLogout={handleResellerAdminLogout} />} />
                <Route path="/ManageClient" element={<ManageClient userInfo={resellerAdminUserInfo} handleLogout={handleResellerAdminLogout} />} />
                <Route path="/managefinance" element={<ManageFinance userInfo={resellerAdminUserInfo} handleLogout={handleResellerAdminLogout} />} />
                <Route path="/profile" element={<Profile userInfo={resellerAdminUserInfo} handleLogout={handleResellerAdminLogout} />} />
              </Routes>
            ) : (
              <Route path="/" element={<Login handleLogin={handleResellerAdminLogin} />} />
            )
          }
        />

        {/* Client Admin Routes */}
        <Route path="/clientadmin/*" element={<ClientAdminApp />} />
      </Routes>
    </Router>
  );
};

export default App;
