import React, { useState } from 'react';
import { Route, Routes, Navigate } from 'react-router-dom';
import Login from '../clientadmin/page/Login';
import Dashboard from '../clientadmin/page/Dashboard';
import ManageDevice from '../clientadmin/page/ManageDevice';
import ManageUsers from '../clientadmin/page/ManageUsers';
import MgManageDevice from '../clientadmin/page/MgManageDevice';
import MgManageUser from '../clientadmin/page/MgManageUser';
import Wallet from '../clientadmin/page/Wallet';
import Profile from '../clientadmin/page/Profile';

const ClientAdminApp = () => {
  const storedUser = JSON.parse(sessionStorage.getItem('clientAdminUser'));
  const [loggedIn, setLoggedIn] = useState(!!storedUser);
  const [userInfo, setUserInfo] = useState(storedUser || {});
  const [initialLoad, setInitialLoad] = useState(true);

  const handleLogin = (data) => {
    const { email, ...rest } = data;
    setUserInfo({ email, ...rest });
    setLoggedIn(true);
    sessionStorage.setItem('clientAdminUser', JSON.stringify({ email, ...rest }));
  };

  const handleLogout = () => {
    setLoggedIn(false);
    setUserInfo({});
    sessionStorage.removeItem('clientAdminUser');
  };

  return (
    <Router>
      <Routes>
        <Route
          path="/"
          element={loggedIn ? <Navigate to="/Dashboard" /> : <Login handleLogin={handleLogin} />}
        />
        <Route
          path="/Dashboard"
          element={loggedIn ? (
            initialLoad ? (
              <Dashboard userInfo={userInfo} handleLogout={handleLogout} setInitialLoad={setInitialLoad} />
            ) : (
              <Dashboard userInfo={userInfo} handleLogout={handleLogout} />
            )
          ) : (
            <Navigate to="/" />
          )}
        />
        <Route
          path="/ManageDevice"
          element={loggedIn ? (
            <ManageDevice userInfo={userInfo} handleLogout={handleLogout} />
          ) : (
            <Navigate to="/" />
          )}
        />
        <Route
          path="/ManageUsers"
          element={loggedIn ? (
            <ManageUsers userInfo={userInfo} handleLogout={handleLogout} />
          ) : (
            <Navigate to="/" />
          )}
        />
        <Route
          path="/MgManageDevice"
          element={loggedIn ? (
            <MgManageDevice userInfo={userInfo} handleLogout={handleLogout} />
          ) : (
            <Navigate to="/" />
          )}
        />
        <Route
          path="/MgManageUser"
          element={loggedIn ? (
            <MgManageUser userInfo={userInfo} handleLogout={handleLogout} />
          ) : (
            <Navigate to="/" />
          )}
        />
        <Route
          path="/Wallet"
          element={loggedIn ? (
            <Wallet userInfo={userInfo} handleLogout={handleLogout} />
          ) : (
            <Navigate to="/" />
          )}
        />
        <Route
          path="/Profile"
          element={loggedIn ? (
            <Profile userInfo={userInfo} handleLogout={handleLogout} />
          ) : (
            <Navigate to="/" />
          )}
        />
      </Routes>
    </Router>
  );
};

export default ClientAdminApp;
