import React, { useEffect } from 'react';
import { Link } from 'react-router-dom';
import $ from 'jquery';

const Header = ({ handleLogout }) => {
  useEffect(() => {
    const handleToggleSidebar = () => {
        const body = $('body');
        if (body.hasClass('sidebar-toggle-display') || body.hasClass('sidebar-absolute')) {
            body.toggleClass('sidebar-hidden');
        } else {
            body.toggleClass('sidebar-icon-only');
        }
    };

    $('[data-toggle="minimize"]').on('click', handleToggleSidebar);

    return () => {
        $('[data-toggle="minimize"]').off('click', handleToggleSidebar);
    };
  }, []); // Empty dependency array ensures effect runs only once on mount

  useEffect(() => {
    const handleOffcanvasToggle = () => {
        $('.sidebar-offcanvas').toggleClass('active');
    };

    $('[data-toggle="offcanvas"]').on('click', handleOffcanvasToggle);

    return () => {
        $('[data-toggle="offcanvas"]').off('click', handleOffcanvasToggle);
    };
  }, []); // Empty dependency array ensures effect runs only once on mount

  useEffect(() => {
    // Ensure correct sidebar state on load
    const body = $('body');
    if (!body.hasClass('sidebar-hidden') && !body.hasClass('sidebar-icon-only')) {
        if (body.hasClass('sidebar-toggle-display') || body.hasClass('sidebar-absolute')) {
            body.addClass('sidebar-hidden');
        } else {
            body.addClass('sidebar-icon-only');
        }
    }
  }, []); // Empty dependency array ensures effect runs only once on mount

  return (
    <nav className="navbar col-lg-12 col-12 p-0 fixed-top d-flex flex-row">
      <div className="text-center navbar-brand-wrapper d-flex align-items-center justify-content-center">
        <Link className="navbar-brand brand-logo mr-5" to="index.html">
          <img src="../../images/dashboard/EV-RESELLER-ADMIN.png" className="mr-2" alt="logo" style={{ paddingLeft: 10 }} />
        </Link>
        <Link className="navbar-brand brand-logo-mini" to="index.html">
          <img src="../../images/dashboard/EV_Logo_16-12-2023.png" alt="logo" />
        </Link>
      </div>
      <div className="navbar-menu-wrapper d-flex align-items-center justify-content-end">
        <button className="navbar-toggler navbar-toggler align-self-center" type="button" data-toggle="minimize">
          <span className="icon-menu"></span>
        </button>
        <ul className="navbar-nav navbar-nav-right">
          <li className="nav-item dropdown">
            <Link className="nav-link count-indicator dropdown-toggle" id="notificationDropdown" to="#" data-toggle="dropdown">
              <i className="icon-ellipsis"></i>
            </Link>
            <div className="dropdown-menu dropdown-menu-right navbar-dropdown preview-list" aria-labelledby="notificationDropdown">
              <button className="dropdown-item" onClick={handleLogout}>
                <i className="ti-power-off text-primary"></i>
                Logout
              </button>
            </div>
          </li>
        </ul>
        <button className="navbar-toggler navbar-toggler-right d-lg-none align-self-center" type="button" data-toggle="offcanvas">
          <span className="icon-menu"></span>
        </button>
      </div>
    </nav>
  );
};

export default Header;
