import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Login from '../reselleradmin/page/Login';
import Dashboard from '../reselleradmin/page/Dashboard';
import ManageDevice from '../reselleradmin/page/ManageDevice';
import ManageClient from '../reselleradmin/page/ManageClient';
import ManageFinance from '../reselleradmin/page/ManageFinance';
import Profile from '../reselleradmin/page/Profile';

const ResellerAdminApp = () => {
  const storedUser = JSON.parse(sessionStorage.getItem('resellerAdminUser'));
  const [loggedIn, setLoggedIn] = useState(!!storedUser);
  const [userInfo, setUserInfo] = useState(storedUser || {});
  const [initialLoad, setInitialLoad] = useState(true);

  const handleLogin = (data) => {
    const { email, ...rest } = data;
    setUserInfo({ email, ...rest });
    setLoggedIn(true);
    sessionStorage.setItem('resellerAdminUser', JSON.stringify({ email, ...rest }));
  };

  const handleLogout = () => {
    setLoggedIn(false);
    setUserInfo({});
    sessionStorage.removeItem('resellerAdminUser');
  };

  return (
    <Router>
      <Routes>
        {/* Route for Login */}
        <Route
          path="/"
          element={loggedIn ? <Navigate to="/Dashboard" /> : <Login handleLogin={handleLogin} />}
        />
        {/* Protected Routes */}
        <Route
          path="/Dashboard"
          element={loggedIn ? (
            initialLoad ? (
              <Dashboard userInfo={userInfo} handleLogout={handleLogout} setInitialLoad={setInitialLoad} />
            ) : (
              <Dashboard userInfo={userInfo} handleLogout={handleLogout} />
            )
          ) : (
            <Navigate to="/" />
          )}
        />
        <Route
          path="/ManageDevice"
          element={loggedIn ? (
            <ManageDevice userInfo={userInfo} handleLogout={handleLogout} />
          ) : (
            <Navigate to="/" />
          )}
        />
        <Route
          path="/ManageClient"
          element={loggedIn ? (
            <ManageClient userInfo={userInfo} handleLogout={handleLogout} />
          ) : (
            <Navigate to="/" />
          )}
        />
        <Route
          path="/ManageFinance"
          element={loggedIn ? (
            <ManageFinance userInfo={userInfo} handleLogout={handleLogout} />
          ) : (
            <Navigate to="/" />
          )}
        />
        <Route
          path="/Profile"
          element={loggedIn ? (
            <Profile userInfo={userInfo} handleLogout={handleLogout} />
          ) : (
            <Navigate to="/" />
          )}
        />
      </Routes>
    </Router>
  );
};

export default ResellerAdminApp;
