// adminApps/SuperAdminApp.js
import React, { useState } from 'react';
import { Route, Routes, Navigate } from 'react-router-dom';
import Login from '../superadmin/page/Login';
import Dashboard from '../superadmin/page/Dashboard';
import ManageDevice from '../superadmin/page/ManageDevice';
import ManageUsers from '../superadmin/page/ManageUsers';
import ManageUserRole from '../superadmin/page/ManageUserRole';
import ManageFinance from '../superadmin/page/ManageFinance';
import ManageReseller from '../superadmin/page/ManageReseller';
import Profile from '../superadmin/page/Profile';

const SuperAdminApp = () => {
  const storedUser = JSON.parse(sessionStorage.getItem('user'));
  const [loggedIn, setLoggedIn] = useState(!!storedUser);
  const [userInfo, setUserInfo] = useState(storedUser || {});
  const [initialLoad, setInitialLoad] = useState(true);

  const handleLogin = (data) => {
    const { email, ...rest } = data;
    setUserInfo({ email, ...rest });
    setLoggedIn(true);
    sessionStorage.setItem('user', JSON.stringify({ email, ...rest }));
  };

  const handleLogout = () => {
    setLoggedIn(false);
    setUserInfo({});
    sessionStorage.removeItem('user');
  };

  return (
    <Routes>
      <Route
        path="/"
        element={loggedIn ? <Navigate to="/Dashboard" /> : <Login handleLogin={handleLogin} />}
      />
      <Route
        path="/Dashboard"
        element={loggedIn ? (
          initialLoad ? (
            <Dashboard userInfo={userInfo} handleLogout={handleLogout} setInitialLoad={setInitialLoad} />
          ) : (
            <Dashboard userInfo={userInfo} handleLogout={handleLogout} />
          )
        ) : (
          <Navigate to="/" />
        )}
      />
      <Route
        path="/ManageDevice"
        element={loggedIn ? (
          <ManageDevice userInfo={userInfo} handleLogout={handleLogout} />
        ) : (
          <Navigate to="/" />
        )}
      />
      <Route
        path="/ManageUsers"
        element={loggedIn ? (
          <ManageUsers userInfo={userInfo} handleLogout={handleLogout} />
        ) : (
          <Navigate to="/" />
        )}
      />
      <Route
        path="/ManageUserRole"
        element={loggedIn ? (
          <ManageUserRole userInfo={userInfo} handleLogout={handleLogout} />
        ) : (
          <Navigate to="/" />
        )}
      />
      <Route
        path="/ManageFinance"
        element={loggedIn ? (
          <ManageFinance userInfo={userInfo} handleLogout={handleLogout} />
        ) : (
          <Navigate to="/" />
        )}
      />
      <Route
        path="/ManageReseller"
        element={loggedIn ? (
          <ManageReseller userInfo={userInfo} handleLogout={handleLogout} />
        ) : (
          <Navigate to="/" />
        )}
      />
      <Route
        path="/Profile"
        element={loggedIn ? (
          <Profile userInfo={userInfo} handleLogout={handleLogout} />
        ) : (
          <Navigate to="/" />
        )}
      />
      <Route path="*" element={<Navigate to="/" />} />
    </Routes>
  );
};

export default SuperAdminApp;
