import React, {useState} from 'react';
import Header from '../components/Header';
import Sidebar from '../components/Sidebar';
import Footer from '../components/Footer';

const Profile = ({ userInfo, handleLogout }) => {

    // const [profile, setProfile] = useState([]);
    // console.log(profile);

    //  useEffect(() => {
    //     const url = '/superadmin/FetchUserProfile';
    //     const fetchData = async () => {
    //         try {
    //             const response = await fetch(url, {
    //                 method: 'POST',
    //                 headers: {
    //                     'Content-Type': 'application/json',
    //                 },
    //                 body: JSON.stringify({ user_id: userInfo.data.user_id }),
    //             });
    //             const result = await response.json();
    //             setProfile(result.data);
    //             console.log('profile', result.data);
    //         } catch (error) {
    //             console.error('Error fetching data:', error);
    //         }
    //     };

    //     fetchData();
    // }, [userInfo.data.user_id]);

    const handleLoginFormSubmit = async (e) => {
        e.preventDefault();
        try {
          const user_id = parseInt(userInfo.data.user_id);
          // Perform API call for login
          const response = await fetch('/superadmin/CheckLoginCredentials', {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json',
            },
            body: JSON.stringify({ user_id:userInfo.data.user_id }),
          });
          // alert(email )
          if (response.ok) {
            const data = await response.json();
            // console.log('Response data:', data); // Log response data to debug
            // Include email in the data sent to handleLogin
            handleLogin({...data}); // Adding email to the data object
          } else {
            // console.error('Login failed');
            setErrorMessage('Login failed. Please check your credentials.');
          }
        } catch (error) {
          setSuccessMessage('');
          if (error.response) {
            setErrorMessage(`Error Status Code: ${error.response.status}`);
          } else {
            setErrorMessage('An error occurred during login. Please try again later.');
          }
        }
      }; 



    // Update profile
    const [username, setUpdateUname] = useState('');
    const [email_id, setUpdateEmail] = useState('');
    const [phone_no, setUpdatePhone] = useState('');
    const [password, setUpdatePassword] = useState('');
    const [errorMessage, setErrorMessage] = useState('');

    const addProfileUpdate = async (e) => {
        alert(  JSON.stringify({ username, email_id, phone_no, password }), 'add charger all data' );
        e.preventDefault();
        try {
            const emailID = parseInt(email_id);
            const phoneNo = parseInt(phone_no);
            const Password = parseInt(password);
            const response = await fetch('/superadmin/UpdateUserProfile', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ user_id:userInfo.data.user_id, username, email_id:emailID, phone_no:phoneNo, password:Password }),
            });
            alert(  JSON.stringify({ user_id:userInfo.data.user_id, username, email_id:emailID, phone_no:phoneNo, password:Password }), 'add charger all data' );
            if (response.ok) {
                alert('Charger added successfully');
                // Clear the input after successful submission if needed
                setUpdateUname(''); 
                setUpdateEmail(''); 
                setUpdatePhone(''); 
                setUpdatePassword(''); 
            } else {
                setErrorMessage('Failed to add charger');
            }
        }catch (error) {
                setErrorMessage('An error occurred while adding the charger');
                console.error('Error:', error);
        }
    };
    // Add Chargers end
    return (
        <div className='container-scroller'>
            {/* Header */}
            {/* <Header/> */}
            <Header userInfo={userInfo} handleLogout={handleLogout}/>
            <div className="container-fluid page-body-wrapper">
                {/* Sidebar */}
                <Sidebar/>
                <div className="main-panel">
                    <div className="content-wrapper">
                        <div className="row">
                            <div className="col-md-12 grid-margin">
                                <div className="row">
                                    <div className="col-12 col-xl-8 mb-4 mb-xl-0">
                                        <h3 className="font-weight-bold">Profile</h3>
                                    </div>
                                </div>
                            </div>
                        </div>
                        <div className="row">
                            <div className="col-lg-12 grid-margin stretch-card">
                                <div className="card">
                                    <div className="card-body">
                                        <div class="col-md-12 grid-margin stretch-card">
                                            <div class="card">
                                                <div class="card-body">
                                                    <form class="forms-sample" onSubmit={addProfileUpdate}>
                                                        <div class="form-group row">
                                                            <label for="exampleInputUsername2" class="col-sm-2 col-form-label">User Name</label>
                                                            <div class="col-sm-10">
                                                                <input type="text" class="form-control" placeholder="Username" value={username}  onChange={(e) => setUpdateUname(e.target.value)} required/>
                                                            </div>
                                                        </div>
                                                        <div class="form-group row">
                                                            <label for="exampleInputEmail2" class="col-sm-2 col-form-label">Email</label>
                                                            <div class="col-sm-10">
                                                                <input type="email" class="form-control" placeholder="Email" value={email_id}  onChange={(e) => setUpdateEmail(e.target.value)} readOnly required/>
                                                            </div>
                                                        </div>
                                                        <div class="form-group row">
                                                            <label for="exampleInputMobile" class="col-sm-2 col-form-label">Phone</label>
                                                            <div class="col-sm-10">
                                                                <input type="text" class="form-control" placeholder="Phone number" pattern="[0-9]{10}" value={phone_no}  onChange={(e) => setUpdatePhone(e.target.value)} required/>
                                                            </div>
                                                        </div>
                                                        <div class="form-group row">
                                                            <label for="exampleInputPassword2" class="col-sm-2 col-form-label">Password</label>
                                                            <div class="col-sm-10">
                                                                <input type="password" class="form-control" pattern="[0-9]{4}" placeholder="Password" value={password}  onChange={(e) => setUpdatePassword(e.target.value)} required/>
                                                            </div>
                                                        </div>
                                                        {errorMessage && <div className="text-danger">{errorMessage}</div>}
                                                        <div style={{textAlign:'center'}}>
                                                            <button type="submit" class="btn btn-primary mr-2">Update</button>
                                                        </div>
                                                    </form>
                                                </div>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                    {/* Footer */}
                    <Footer />
                </div>         
            </div>    
        </div>
    );
};   

export default Profile
