import React, {useState, useEffect} from 'react';
import axios from 'axios';
import Header from '../components/Header';
import Sidebar from '../components/Sidebar';
import Footer from '../components/Footer';

const ManageUserRole = ({ userInfo, handleLogout }) => {

    const [data, setData] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [filteredData] = useState([]);
    const [posts, setPosts] = useState([]);

    // Get manage user role
    useEffect(() => {
        // Define the API URL based on the event detail
        const url = `/superadmin/FetchUserRoles`;
        axios.get(url).then((res) => {
            // console.log('Data fetched successfully:', res.data);
            setData(res.data.data); // Assuming the data you need is inside the 'data' property
            setLoading(false);
            // console.log(JSON.stringify(res.data) + ' charger data');
        })
        .catch((err) => {
            console.error('Error fetching data:', err);
            setError('Error fetching data. Please try again.');
            setLoading(false);
        });
    }, []);

    // Add user role start 
    const [showAddForm, setShowAddForm] = useState(false);

    const addChargers = () => {
        setShowAddForm(prevState => !prevState); // Toggles the form visibility
    };
    const closeAddModal = () => {
        setShowAddForm(false); // Close the form
    };
    const modalAddStyle = {
        display: showAddForm ? 'block' : 'none',
    }

    // Add user role
    const [rolename, setuserRole] = useState('');
    const [errorMessage, setErrorMessage] = useState('');

    const addUserRole = async (e) => {
        e.preventDefault();
        try {
            const response = await fetch('/superadmin/CreateUserRole', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ rolename, created_by: userInfo.email}),
            });
            alert(  JSON.stringify({ rolename, created_by: userInfo.email}) );
            if (response.ok) {
                alert('Add user role successfully');
                setuserRole(''); 
                setShowAddForm(false);
                closeAddModal();
            } else {
                setErrorMessage('Failed to user role');
            }
        }catch (error) {
                setErrorMessage('An error occurred while adding user role');
                console.error('Error:', error);
        }
    };

    return (
        <div className='container-scroller'>
            {/* Header */}
            <Header userInfo={userInfo} handleLogout={handleLogout} />
            <div className="container-fluid page-body-wrapper">
                {/* Sidebar */}
                <Sidebar/>
                <div className="main-panel">
                    <div className="content-wrapper">
                        <div className="row">
                            <div className="col-md-12 grid-margin">
                                <div className="row">
                                    <div className="col-12 col-xl-8 mb-4 mb-xl-0">
                                        <h3 className="font-weight-bold">Manage User Role's</h3>
                                    </div>
                                    <div className="col-12 col-xl-4">
                                        <div className="justify-content-end d-flex">
                                            <button type="button" className="btn btn-success" onClick={addChargers}>Add Role's</button>
                                            {/* Add role start */}
                                            <div className="modalStyle" style={modalAddStyle}>
