import React, { useState } from 'react';
import Header from '../components/Header';
import Sidebar from '../components/Sidebar';
import Footer from '../components/Footer';

const ManageUserRole = ({ userInfo, handleLogout }) => {
    // Add Chargers start 
    const [showAddForm, setShowAddForm] = useState(false);

    const addChargers = () => {
        setShowAddForm(prevState => !prevState); // Toggles the form visibility
    };
    const closeAddModal = () => {
        setShowAddForm(false); // Close the form
    };
    const modalAddStyle = {
        display: showAddForm ? 'block' : 'none',
    }

      // Add charger details in server
      const [userRole, setuserRole] = useState('');
      const [errorMessage, setErrorMessage] = useState('');

      const addChargerSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await fetch('http://192.168.1.70:9090/GetAllinputData', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ userRole }),
            });
            alert(  JSON.stringify({ userRole }), 'add charger all data' );
            if (response.ok) {
                alert('Charger added successfully');
                // Clear the input after successful submission if needed
                setuserRole(''); 
                setShowAddForm(false); // Close the form after successful addition
            } else {
                setErrorMessage('Failed to add charger');
            }
        }catch (error) {
                setErrorMessage('An error occurred while adding the charger');
                console.error('Error:', error);
        }
    };
    // Add Chargers end

     //  Edit Info box start
     const [showForm, setShowForm] = useState(false);

     const toggleForm = () => {
         setShowForm(prevState => !prevState); // Toggles the form visibility
     };

     const closeModal = () => {
         setShowForm(false); // Close the form
     };
     const modalStyle = {
         display: showForm ? 'block' : 'none',
     }
     // Edit Info box end

    return (
        <div className='container-scroller'>
            {/* Header */}
            {/* <Header/> */}
            <Header userInfo={userInfo} handleLogout={handleLogout} />
            <div className="container-fluid page-body-wrapper">
                {/* Sidebar */}
                <Sidebar/>
                <div className="main-panel">
                    <div className="content-wrapper">
                        <div className="row">
                            <div className="col-md-12 grid-margin">
                                <div className="row">
                                    <div className="col-12 col-xl-8 mb-4 mb-xl-0">
                                        <h3 className="font-weight-bold">Manage User Role's</h3>
                                    </div>
                                    <div className="col-12 col-xl-4">
                                        <div className="justify-content-end d-flex">
                                            <button type="button" className="btn btn-success" onClick={addChargers}>Add Role's</button>
                                            {/* Edit Info box start */}
                                            <div className="modalStyle" style={modalAddStyle}>
                                                <div className="modalContentStyle" style={{ maxHeight: '680px', overflowY: 'auto' }}>
                                                    <span onClick={closeAddModal} style={{ float: 'right', cursor: 'pointer', fontSize:'30px' }}>&times;</span>
                                                    <form className="pt-3" onSubmit={addChargerSubmit}>
                                                        <div className="card-body">
                                                            <div style={{textAlign:'center'}}>
                                                                <h4 className="card-title">Add Role's</h4>
                                                            </div>
                                                            <div className="table-responsive pt-3">
                                                                <div className="input-group">
                                                                    <div className="input-group-prepend">
                                                                        <span className="input-group-text" style={{color:'black', width:'125px'}}>Role Name</span>
                                                                    </div>
                                                                    <input type="text" className="form-control" value={userRole}  onChange={(e) => setuserRole(e.target.value)} required/>
                                                                </div>
                                                            </div>
                                                            {errorMessage && <div className="text-danger">{errorMessage}</div>}
                                                            <button type="submit" className="btn btn-primary mr-2" style={{marginTop:'10px'}}>Add</button>
                                                            <button className="btn btn-danger" onClick={closeAddModal} style={{marginTop:'10px'}}>Cancel</button>
                                                        </div>
                                                    </form>
                                                </div>
                                            </div>
                                            {/* Add user role end */}
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                        <div className="row">
                            <div className="col-lg-12 grid-margin stretch-card">
                                <div className="card">
                                    <div className="card-body">
                                        <div className="row">
                                            <div className="col-md-12 grid-margin">
                                                <div className="row">
                                                    <div className="col-4 col-xl-8">
                                                        <h4 className="card-title" style={{paddingTop:'10px'}}>List Of Role's</h4>  
                                                    </div>
                                                    <div className="col-8 col-xl-4">
                                                        <div className="input-group">
                                                            <div className="input-group-prepend hover-cursor" id="navbar-search-icon">
                                                                <span className="input-group-text" id="search">
                                                                <i className="icon-search"></i>
                                                                </span>
                                                            </div>
                                                            <input type="text" className="form-control" placeholder="Search now" aria-label="search" aria-describedby="search" autoComplete="off"/>
                                                        </div>
                                                    </div>
                                                </div>
                                            </div>
                                        </div>
                                        <div className="table-responsive">
                                            <table className="table table-striped">
                                                <thead style={{textAlign:'center'}}>
                                                    <tr> 
                                                        <th>Sl.No</th>
                                                        <th>Role ID</th>
                                                        <th>Role Name</th>
                                                        <th>Created Date</th>
                                                        <th>Created By</th>
                                                        <th>Modified Date</th>
                                                        <th>Modified By</th>
                                                        <th>Status</th>
                                                        <th>Option</th>
                                                    </tr>
                                                </thead>
                                                <tbody style={{textAlign:'center'}}>
                                                    <tr>
                                                        <td>1</td>
                                                        <td>000231</td>
                                                        <td>SparkMinda</td>
                                                        <td>21/06/2024</td>
                                                        <td>Supser Admin</td>
                                                        <td>21/06/2024</td>
                                                        <td>SA</td>
                                                        <td>Ok</td>
                                                        <td><button type="button" className=" btn btn-outline-success btn-icon-text" onClick={toggleForm}><i className="mdi mdi-pencil btn-icon-prepend"></i>Edit</button></td>
                                                    </tr>
                                                    {/* Edit Info box start */}
                                                    <div className="modalStyle" style={modalStyle}>
                                                        <div className="modalContentStyle" style={{ maxHeight: '650px', overflowY: 'auto' }}>
                                                            <span onClick={closeModal} style={{ float: 'right', cursor: 'pointer', fontSize:'30px' }}>&times;</span>
                                                            <div className="card">
                                                                <div className="card-body">
                                                                    <h4 className="card-title">Edit User's</h4>
                                                                    <div className="table-responsive pt-3">
                                                                        <div className="input-group">
                                                                            <div className="input-group-prepend">
                                                                                <span className="input-group-text" style={{color:'black', width:'125px'}}>Role Name</span>
                                                                            </div>
                                                                            <input type="text" className="form-control" />
                                                                        </div>
                                                                        <div className="input-group">
                                                                            <div className="input-group-prepend">
                                                                                <span className="input-group-text" style={{color:'black', width:'125px'}}>Status</span>
                                                                            </div>
                                                                            <select className="form-control">
                                                                                <option>Allocated</option>
                                                                                <option>Unallocated</option>
                                                                            </select>
                                                                        </div>
                                                                    </div>
                                                                    <button type="submit" className="btn btn-primary mr-2" style={{marginTop:'10px'}}>Submit</button>
                                                                    <button className="btn btn-danger" onClick={closeModal} style={{marginTop:'10px'}}>Cancel</button>
                                                                </div>
                                                            </div>
                                                        </div>
                                                    </div>
                                                    {/* Edit Info box end */}
                                                </tbody>
                                            </table>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                    {/* Footer */}
                    <Footer />
                </div>         
            </div>    
        </div>
    )
}   

export default ManageUserRole
