import React, { useState, useEffect} from 'react';
import axios from 'axios';
import Header from '../../components/Header';
import Sidebar from '../../components/Sidebar';
import Footer from '../../components/Footer';
import { useNavigate } from 'react-router-dom';
import Swal from 'sweetalert2';

const ManageUsers = ({ userInfo, handleLogout }) => {
    const navigate = useNavigate();

    const handleViewUser = (dataItem) => {
        navigate('/superadmin/ViewUserList', { state: { dataItem } });
    };

    const handleEditUser = (dataItem) => {
        navigate('/superadmin/EditUserList', { state: { dataItem } });
    };

    // Add Chargers start 
    const [showAddForm, setShowAddForm] = useState(false);

    const addChargers = () => {
        setShowAddForm(prevState => !prevState);
    };
    const closeAddModal = () => {
        setShowAddForm(false);
    };
    const modalAddStyle = {
        display: showAddForm ? 'block' : 'none',
    }

    // Add Manage User
    const [role, setRole] = useState({ role_id: '', role_name: '' });
    const [reseller_id, setSelectedReseller] = useState('');
    const [username, setuserName] = useState('');
    const [email_id, setemailID] = useState('');
    const [Password, setPassword] = useState('');
    const [phoneNo, setPhone] = useState('');
    const [walletBal, setWallet] = useState('');
    const [errorMessage, setErrorMessage] = useState('');
    const [updateTrigger, setUpdateTrigger] = useState(false);

    const handleResellerChange = (e) => {
        const [role_id, role_name] = e.target.value.split('|');
        setRole({ role_id, role_name });
    };

    const handleselectionReseller = (e) => {
        setSelectedReseller(e.target.value);
    };

    const addManageUser = async (e) => {
        e.preventDefault();
        try {
            const roleID = parseInt(role.role_id);
            const resellerID = parseInt(reseller_id);
            const password = parseInt(Password);
            const phone_no = parseInt(phoneNo);
            const wallet_bal = parseInt(walletBal);
            const response = await fetch('/superadmin/CreateUser', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ role_id:roleID, reseller_id:resellerID, username, email_id, password, phone_no, wallet_bal, created_by:userInfo.data.username }),
            });
            if (response.ok) {
                Swal.fire({
                    title: "Charger added successfully",
                    icon: "success"
                });
                setuserName(''); 
                setemailID(''); 
                setPassword(''); 
                setPhone(''); 
                setWallet(''); 
                setShowAddForm(false);
            } else {
                Swal.fire({
                    title: "Error",
                    text: "Failed to add charger",
                    icon: "error"
                });
            }
        }catch (error) {
            Swal.fire({
                title: "Error:", error,
                text: "An error occurred while updating user status.",
                icon: "error"
            });
                setErrorMessage('An error occurred while adding the charger');
                console.error('Error:', error);
        }
    };
    // Add Manage User end

    const [selectionRoles, setSelectionRoles] = useState([]);
    const [selectionReseller, setSelectionReseller] = useState([]);

    useEffect(() => {
        const url = '/superadmin/FetchSpecificUserRoleForSelection';
        axios.get(url)
            .then((res) => {
                setSelectionRoles(res.data.data);
            })
            .catch((err) => {
                console.error('Error fetching data:', err);
            });
    }, []);

    useEffect(() => {
        const url = '/superadmin/FetchResellerForSelection';
        axios.get(url)
            .then((res) => {
                setSelectionReseller(res.data.data);
            })
            .catch((err) => {
                console.error('Error fetching data:', err);
            });
    }, []);

    const [data, setData] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [filteredData] = useState([]);
    const [posts, setPosts] = useState([]);

    // Get manage user data
    useEffect(() => {
        const url = `/superadmin/FetchUsers`;
        axios.get(url).then((res) => {
            setData(res.data.data);
            setLoading(false);
        })
           .catch((err) => {
            console.error('Error fetching data:', err);
            setError('Error fetching data. Please try again.');
            setLoading(false);
          });
    }, [updateTrigger]);

    // Search data 
    const handleSearchInputChange = (e) => {
        const inputValue = e.target.value.toUpperCase();
        if (Array.isArray(data)) {
            const filteredData = data.filter((item) =>
                item.username.toUpperCase().includes(inputValue)
            );
            setPosts(filteredData);
        }
    };

    // Update table data 'data', and 'filteredData' 
    useEffect(() => {
        switch (data) {
            case 'filteredData':
                setPosts(filteredData);
                break;
            default:
                setPosts(data);
                break;
        }
    }, [data, filteredData]);

    // Timestamp data 
    function formatTimestamp(originalTimestamp) {
        const date = new Date(originalTimestamp);
        const day = String(date.getDate()).padStart(2, '0');
        const month = String(date.getMonth() + 1).padStart(2, '0');
        const year = date.getFullYear();
        let hours = date.getHours();
        const minutes = String(date.getMinutes()).padStart(2, '0');
        const seconds = String(date.getSeconds()).padStart(2, '0');
        const ampm = hours >= 12 ? 'PM' : 'AM';
        hours = hours % 12;
        hours = hours ? hours : 12; // the hour '0' should be '12'
        hours = String(hours).padStart(2, '0');
        const formattedDate = `${day}/${month}/${year} ${hours}:${minutes}:${seconds} ${ampm}`;
        return formattedDate;
    } 

    const changeDeActivate = async (e, user_id) => {
        e.preventDefault();
        try {
            const response = await fetch('/superadmin/DeActivateUser', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ user_id, status:false, modified_by: userInfo.data.username }),
            });
            if (response.ok) {
                alert('DeActivate successfully');
                setUpdateTrigger((prev) => !prev);
            } else {
                setErrorMessage('Failed to DeActivate');
            }
        }catch (error) {
                setErrorMessage('An error occurred while adding DeActivate');
                console.error('Error:', error);
        }
    };

    const changeActivate = async (e, user_id) => {
        e.preventDefault();
        try {
            const response = await fetch('/superadmin/DeActivateUser', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ user_id, status:true, modified_by: userInfo.data.username }),
            });
            if (response.ok) {
                alert('Activate successfully');
                setUpdateTrigger((prev) => !prev);
            } else {
                setErrorMessage('Failed to Activate');
            }
        }catch (error) {
                setErrorMessage('An error occurred while adding Activate');
                console.error('Error:', error);
        }
    };

    return (
        <div className='container-scroller'>
            {/* Header */}
            <Header userInfo={userInfo} handleLogout={handleLogout} />
            <div className="container-fluid page-body-wrapper">
                {/* Sidebar */}
                <Sidebar/>
                <div className="main-panel">
                    <div className="content-wrapper">
                        <div className="row">
                            <div className="col-md-12 grid-margin">
                                <div className="row">
                                    <div className="col-12 col-xl-8 mb-4 mb-xl-0">
                                        <h3 className="font-weight-bold">Manage User's</h3>
                                    </div>
                                    <div className="col-12 col-xl-4">
                                        <div className="justify-content-end d-flex">
                                            <button type="button" className="btn btn-success" onClick={addChargers}>Add User's</button>
                                            {/* Add user start */}
                                            <div className="modalStyle" style={modalAddStyle}>
                                                <div className="modalContentStyle" style={{ maxHeight: '680px', overflowY: 'auto' }}>
                                                    <span onClick={closeAddModal} style={{ float: 'right', cursor: 'pointer', fontSize:'30px' }}>&times;</span>
                                                    <form className="card" onSubmit={addManageUser}>
                                                        <div className="card-body">
                                                            <div style={{textAlign:'center'}}>
                                                                <h4 className="card-title" style={{alignItems:'center'}}>Add User's</h4>
                                                            </div>
                                                            <div className="table-responsive pt-3">
                                                                <div className="input-group">
                                                                    <div className="input-group-prepend">
                                                                        <span className="input-group-text" style={{color:'black', width:'125px'}}>Role</span>
                                                                    </div>
                                                                    <select className="form-control" value={`${role.role_id}|${role.role_name}`} onChange={handleResellerChange}>
                                                                        <option value="">Select Admin</option>
                                                                        {selectionRoles.map((role, index) => (
                                                                            <option key={index} value={`${role.role_id}|${role.role_name}`}>{role.role_name}</option>
                                                                        ))}
                                                                    </select>                                                               
                                                                </div>

                                                                {role.role_name === 'reselleradmin' && (
                                                                <div className="input-group">
                                                                    <div className="input-group-prepend">
                                                                        <span className="input-group-text" style={{color:'black', width:'125px'}}>Reseller</span>
                                                                    </div>
                                                                    <select className="form-control" value={reseller_id} onChange={handleselectionReseller}>
                                                                        <option value="">Select Reseller</option>
                                                                        {selectionReseller.map((roles, index) => (
                                                                            <option key={index} value={roles.reseller_id}>{roles.reseller_name}</option>
                                                                        ))}
                                                                    </select>                                                               
                                                                </div>
                                                                )}
                                                                <div className="input-group">
                                                                    <div className="input-group-prepend">
                                                                        <span className="input-group-text" style={{color:'black', width:'125px'}}>User Name</span>
                                                                    </div>
                                                                    <input type="text" className="form-control" placeholder="User Name" value={username} onChange={(e) => setuserName(e.target.value)} required/>
                                                                </div>
                                                                <div className="input-group">
                                                                    <div className="input-group-prepend">
                                                                        <span className="input-group-text" style={{color:'black', width:'125px'}}>Emial ID</span>
                                                                    </div>
                                                                    <input type="email" className="form-control" placeholder="Email ID" value={email_id} onChange={(e) => setemailID(e.target.value)} required/>
                                                                </div>
                                                                <div className="input-group">
                                                                    <div className="input-group-prepend">
                                                                        <span className="input-group-text" style={{color:'black', width:'125px'}}>Phone</span>
                                                                    </div>
                                                                    <input type="phone" className="form-control" placeholder="Phone" value={phoneNo} onChange={(e) => setPhone(e.target.value)} required/>
                                                                </div>
                                                                <div className="input-group">
                                                                    <div className="input-group-prepend">
                                                                        <span className="input-group-text" style={{color:'black', width:'125px'}}>Password</span>
                                                                    </div>
                                                                    <input type="password" className="form-control" placeholder="Password" value={Password} onChange={(e) => setPassword(e.target.value)} required/>
                                                                </div>
                                                                <div className="input-group">
                                                                    <div className="input-group-prepend">
                                                                        <span className="input-group-text" style={{color:'black', width:'125px'}}>Wallet</span>
                                                                    </div>
                                                                    <input type="text" className="form-control" placeholder="Wallet" value={walletBal} onChange={(e) => setWallet(e.target.value)} required/>
                                                                </div>
                                                            </div>
                                                            {errorMessage && <div className="text-danger">{errorMessage}</div>}
                                                            <div style={{textAlign:'center'}}>
                                                                <button type="submit" className="btn btn-primary mr-2" style={{marginTop:'10px'}}>Add</button>
                                                            </div>
                                                        </div>
                                                    </form>
                                                </div>
                                            </div>
                                            {/* Add users end */}
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                        <div className="row">
                            <div className="col-lg-12 grid-margin stretch-card">
                                <div className="card">
                                    <div className="card-body">
                                        <div className="row">
                                            <div className="col-md-12 grid-margin">
                                                <div className="row">
                                                    <div className="col-4 col-xl-8">
                                                        <h4 className="card-title" style={{paddingTop:'10px'}}>List Of User's</h4>  
                                                    </div>
                                                    <div className="col-8 col-xl-4">
                                                        <div className="input-group">
                                                            <div className="input-group-prepend hover-cursor" id="navbar-search-icon">
                                                                <span className="input-group-text" id="search">
                                                                <i className="icon-search"></i>
                                                                </span>
                                                            </div>
                                                            <input type="text" className="form-control" placeholder="Search now" aria-label="search" aria-describedby="search" autoComplete="off"  onChange={handleSearchInputChange}/>
                                                        </div>
                                                    </div>
                                                </div>
                                            </div>
                                        </div>
                                        <div className="table-responsive">
                                            <table className="table table-striped">
                                                <thead style={{textAlign:'center'}}>
                                                    <tr> 
                                                        <th>Sl.No</th>
                                                        <th>User Name</th>
                                                        <th>Email ID</th>
                                                        <th>Assigned Reseller</th>
                                                        <th>Assigned Client</th>
                                                        <th>Assigned Association</th>
                                                        <th>Created By</th>
                                                        <th>Created Date</th>
                                                        <th>Status</th>
                                                        <th>Active/DeActive</th>
                                                        <th>Option</th>
                                                    </tr>
                                                </thead>
                                                <tbody style={{textAlign:'center'}}>
                                                    {loading ? (
                                                        <tr>
                                                            <td colSpan="11" style={{ marginTop: '50px', textAlign: 'center' }}>Loading...</td>
                                                        </tr>
                                                    ) : error ? (
                                                        <tr>
                                                            <td colSpan="11" style={{ marginTop: '50px', textAlign: 'center' }}>Error: {error}</td>
                                                        </tr>
                                                    ) : (
                                                        Array.isArray(posts) && posts.length > 0 ? (
                                                            posts.map((dataItem, index) => (
                                                            <tr key={index}>
                                                                <td>{index + 1}</td>
                                                                <td>{dataItem.username}</td>
                                                                <td>{dataItem.email_id}</td>
                                                                <td>{dataItem.reseller_id}</td>
                                                                <td>{dataItem.client_id}</td>
                                                                <td>{dataItem.association_id}</td>
                                                                <td>{dataItem.created_by}</td> 
                                                                <td>{formatTimestamp(dataItem.created_date)}</td>
                                                                <td>{dataItem.status===true ? <span className="text-success">Active</span> : <span className="text-danger">DeActive</span>}</td>
                                                                <td>
                                                                    <div className='form-group' style={{paddingTop:'13px'}}> 
                                                                        {dataItem.status===true ?
                                                                            <div className="form-check form-check-danger">
                                                                                <label className="form-check-label"><input type="radio" className="form-check-input" name="optionsRadios1" id="optionsRadios2" value={false} onClick={(e) => changeDeActivate(e, dataItem.user_id)}/>DeActive<i className="input-helper"></i></label>
                                                                            </div>
                                                                        :
                                                                            <div className="form-check form-check-success">
                                                                                <label className="form-check-label"><input type="radio" className="form-check-input" name="optionsRadios1" id="optionsRadios1" value={true} onClick={(e) => changeActivate(e, dataItem.user_id)}/>Active<i className="input-helper"></i></label>
                                                                            </div>
                                                                        }
                                                                    </div>
                                                                </td>      
                                                                <td>
                                                                    <button type="button" className="btn btn-outline-success btn-icon-text"  onClick={() => handleViewUser(dataItem)} style={{marginBottom:'10px', marginRight:'10px'}}><i className="mdi mdi-eye"></i>View</button> 
                                                                    <button type="button" className="btn btn-outline-primary btn-icon-text"  onClick={() => handleEditUser(dataItem)} style={{marginBottom:'10px', marginRight:'10px'}}><i className="mdi mdi-pencil btn-icon-prepend"></i>Edit</button><br/>
                                                                </td>
                                                            </tr>
                                                        ))
                                                        ) : (
                                                            <tr>
                                                                <td colSpan="11" style={{ marginTop: '50px', textAlign: 'center' }}>No devices found</td>
                                                            </tr>
                                                        )
                                                    )}
                                                </tbody>
                                            </table>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                    {/* Footer */}
                    <Footer />
                </div>         
            </div>    
        </div>
    )
}   

export default ManageUsers
